if strcmp(P.Mode,'Image') % Correction of the image ('Undistort' the distorted image)
  
  [iX,iY] = meshgrid(1:P.Resolution(1),1:P.Resolution(2));
  
  RadialAxisX = iX - HalfX; RadialAxisY = iY - HalfY;
  Dist = sqrt((RadialAxisX - .5) .^ 2 + (RadialAxisY - .5) .^ 2);
  R = Dist ./ CorrectionRadius;
  
  if ~all(R); keyboard; else; Theta = atan(R) ./ R; end
  RadialSourceX = HalfX + Theta .* RadialAxisX .* P.ZoomX;
  RadialSourceY = HalfY + Theta .* RadialAxisY .* P.ZoomY;
  
  PerspectiveAxisX = RadialSourceX - P.Axes(1); PerspectiveAxisY = RadialSourceY - P.Axes(2);
  CombinedSourceX = RadialSourceX - P.TranslationX - P.ZoomX .* PerspectiveAxisX ./ max(P.Resolution(1)-P.Axes(1),P.Axes(1)) .* (P.PerspectiveStrength(1) ./ (P.Platform(3)-P.Platform(4)) .* RadialSourceY - P.PerspectiveStrength(1) .* P.Platform(4) ./ (P.Platform(3) - P.Platform(4)));
  CombinedSourceY = RadialSourceY - P.ZoomY .* PerspectiveAxisY ./ max(P.Resolution(2)-P.Axes(2),P.Axes(2)) .* (P.PerspectiveStrength(2) ./ (P.Platform(2)-P.Platform(1)) .* RadialSourceX - P.PerspectiveStrength(2) .* P.Platform(1) ./ (P.Platform(2) - P.Platform(1)));
  
  CombinedSourceX = min(max(CombinedSourceX,1),size(Data,2));
  CombinedSourceY = min(max(CombinedSourceY,1),size(Data,1));
  
  ContributionR = CombinedSourceX - floor(CombinedSourceX);
  ContributionL = 1 - ContributionR;
  
  ContributionB = CombinedSourceY - floor(CombinedSourceY);
  ContributionT = 1 - ContributionB;
  
  ColorTL = cat(3,Data(floor(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)),Data(P.Resolution(1)*P.Resolution(2)+floor(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)),Data(2*P.Resolution(1)*P.Resolution(2)+floor(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)));
  ColorTR = cat(3,Data(floor(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)),Data(P.Resolution(1)*P.Resolution(2)+floor(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)),Data(2*P.Resolution(1)*P.Resolution(2)+floor(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)));
  ColorBL = cat(3,Data(ceil(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)),Data(P.Resolution(1)*P.Resolution(2)+ceil(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)),Data(2*P.Resolution(1)*P.Resolution(2)+ceil(CombinedSourceY)+(floor(CombinedSourceX)-1)*P.Resolution(2)));
  ColorBR = cat(3,Data(ceil(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)),Data(P.Resolution(1)*P.Resolution(2)+ceil(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)),Data(2*P.Resolution(1)*P.Resolution(2)+ceil(CombinedSourceY)+(ceil(CombinedSourceX)-1)*P.Resolution(2)));
  
  ContributionTL = ContributionT + ContributionL;
  ContributionTR = ContributionT + ContributionR;
  ContributionBL = ContributionB + ContributionL;
  ContributionBR = ContributionB + ContributionR;
  
  DataCorrected = uint8((double(ColorTL) .* ContributionTL + double(ColorTR) .* ContributionTR + double(ColorBL) .* ContributionBL + double(ColorBR) .* ContributionBR) ./ 4);

end
